sap.ui.define(["sap/ui/test/opaQunit","./pages/Master"],function(e){"use strict";QUnit.module("Master List");e("Should see the master list with all entries",function(e,t,i){e.iStartMyApp();i.onTheMasterPage.iShouldSeeTheList().and.theListShouldHaveAllEntries().and.theHeaderShouldDisplayAllEntries().and.theListShouldContainOnlyFormattedUnitNumbers()});e("Search for the First object should deliver results that contain the firstObject in the name",function(e,t,i){t.onTheMasterPage.iSearchForTheFirstObject();i.onTheMasterPage.theListShowsOnlyObjectsWithTheSearchStringInTheirTitle()});e("Entering something that cannot be found into search field and pressing search field's refresh should leave the list as it was",function(e,t,i){t.onTheMasterPage.iTypeSomethingInTheSearchThatCannotBeFoundAndTriggerRefresh();i.onTheMasterPage.theListHasEntries()});e("Entering something that cannot be found into search field and pressing 'search' should display the list's 'not found' message",function(e,t,i){t.onTheMasterPage.iSearchForSomethingWithNoResults();i.onTheMasterPage.iShouldSeeTheNoDataTextForNoSearchResults().and.theListHeaderDisplaysZeroHits()});e("Should display items again if the searchfield is emptied",function(e,t,i){t.onTheMasterPage.iClearTheSearch();i.onTheMasterPage.theListShouldHaveAllEntries()});e("MasterList Sorting on Name",function(e,t,i){t.onTheMasterPage.iSortTheListOnName();i.onTheMasterPage.theListShouldBeSortedAscendingOnName()});e("MasterList Filtering on UnitNumber less than 100",function(e,t,i){t.onTheMasterPage.iFilterTheListOnUnitNumber();i.onTheMasterPage.theListShouldBeFilteredOnUnitNumber()});e("MasterList remove filter should display all items",function(e,t,i){t.onTheMasterPage.iOpenViewSettingsDialog().and.iPressResetInViewSelectionDialog().and.iPressOKInViewSelectionDialog();i.onTheMasterPage.theListShouldHaveAllEntries()});e("MasterList Sorting on UnitNumber",function(e,t,i){t.onTheMasterPage.iSortTheListOnUnitNumber();i.onTheMasterPage.theListShouldBeSortedAscendingOnUnitNumber()});e("MasterList grouping created group headers",function(e,t,i){t.onTheMasterPage.iGroupTheList();i.onTheMasterPage.theListShouldContainAGroupHeader()});e("Remove grouping from MasterList delivers initial list",function(e,t,i){t.onTheMasterPage.iRemoveListGrouping();i.onTheMasterPage.theListShouldNotContainGroupHeaders().and.theListShouldHaveAllEntries()});e("Grouping the master list and sorting it should deliver the initial list",function(e,t,i){t.onTheMasterPage.iGroupTheList().and.iSortTheListOnUnitNumber();i.onTheMasterPage.theListShouldContainAGroupHeader();i.iTeardownMyApp()})});